targets.query:
let ForecastDays = 14d; 
let StartTime = now();
let EndTime =  now() + ForecastDays;
let Interval = $granularita;
wallet
| where initiativeId == "$initiativeId"
| where status != "UNSUBSCRIBED" 
| where voucherEndDate between (StartTime .. EndTime)
| extend ResiduoWallet = amountCents
| where ResiduoWallet > 0
| summarize RientroPrevisto = sum(amountCents)/100.0 by bin(voucherEndDate, Interval)
| project Giorno = format_datetime(voucherEndDate, 'yyyy-MM-dd'), RientroPrevisto
| order by Giorno asc
